import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import deviceService from "./deviceService"

const initialState = {
    devices: null,
    isError: false,
    message: ''
}

export const getMy = createAsyncThunk('device/get', async (user, thunkAPI) => {
    try {
        return await deviceService.getMy(user)
    } catch (error) {
        const message = error.response.data['error']
        return thunkAPI.rejectWithValue(message)
    }
})

export const register = createAsyncThunk('device/register', async (user, thunkAPI) => {
    try {
        return await deviceService.register(user)
    } catch (error) {
        const message = error.response.data['error']
        return thunkAPI.rejectWithValue(message)
    }
})

export const remove = createAsyncThunk('device/remove', async (user, thunkAPI) => {
    try {
        return await deviceService.remove(user)
    } catch (error) {
        const message = error.response.data['error']
        return thunkAPI.rejectWithValue(message)
    }
})



export const deviceSlice = createSlice({
    name: "device",
    initialState,
    reducers: {

    },
    extraReducers: (builder) => {
        builder
            .addCase(getMy.fulfilled, (state, action) => {
                state.isError = false
                console.log(action.payload)
            })
            .addCase(getMy.rejected, (state, action) => {
                state.isError = true
                console.log(action.payload)
            })
            .addCase(register.fulfilled, (state, action) => {
                state.isError = false
                console.log(action.payload)
            })
            .addCase(register.rejected, (state, action) => {
                state.isError = true
                console.log(action.payload)
            })
            .addCase(remove.fulfilled, (state, action) => {
                state.isError = false
                console.log(action.payload)
            })
            .addCase(remove.rejected, (state, action) => {
                state.isError = true
                console.log(action.payload)
            })
    }
})


export default deviceSlice.reducer
