import { useState } from 'react'
import { format } from './Appointments'

export default function MyAppointments() {
    const [appts, setAppts] = useState([{
        id: 0,
        date: "today",
        machine: 0,
        timeslot: 0,
        room: 410
    },
    {
        id: 1,
        date: "today",
        machine: 1,
        timeslot: 0,
        room: 410
    },
    {
        id: 3,
        date: "today",
        machine: 1,
        timeslot: 2,
        room: 1705
    }])

    function onDelete(id) {
        // Cancel the appointmen on the server and refresh the table
        // id = appointment id
        console.log(`Canceling appointment with id ${id}`)
    }

    return (
        <>
            <div className="text-center">
                <h3 className="mb-3">My Appointmets</h3>
                <MyAppointmentsTable appts={appts} onDelete={onDelete} />
            </div>
        </>
    )
}

function MyAppointmentsTable({ appts, onDelete }) {
    let rows = []
    for (let id in appts) {
        rows.push(
            <MyAppointmentsTableRow key={appts[id].id} appt={appts[id]} onDelete={onDelete} />
        )
    }
    return (
        <table className="table table-striped fs-5">
            <thead>
                <tr>
                    <th>date</th>
                    <th>timeslot</th>
                    <th>machine</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {rows}
            </tbody>
        </table>

    )
}

function MyAppointmentsTableRow({ appt, onDelete }) {
    return (
        <tr>
            <td>
                {appt.date}
            </td>
            <td>
                {format(appt.timeslot)}
            </td>
            <td>
                {appt.machine}
            </td>
            <td>
                <button data-id={appt.id} type="button" className="btn btn-outline-secondary" onClick={(e) => { onDelete(e.target.dataset.id) }}>cancel</button>
            </td>
        </tr>
    )
}
