import { Routes, Route, Navigate, Outlet } from 'react-router-dom'

import './App.css'

import NavBar from './components/NavBar'
import Login from './pages/Login'
import Register from './pages/Register'
import Devices from './pages/Devices'
import AddDevice from './pages/AddDevice'
import MyAppointments from './pages/MyAppointments'
import Appointments from './pages/Appointments'
import Dashboard from './pages/Dashboard'

import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'
import { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { isAuth } from './features/auth/authSlice'

function App() {

  const dispatch = useDispatch()
  useEffect(() => {
    try {
      dispatch(isAuth())
    } catch (error) {
      console.log(error)
    }
  }, [dispatch, isAuth])

  const { message } = useSelector((state) => state.auth)
  useEffect(() => {
    if (message != '') {
      toast(message)
    }
  }, [message,])


  const { isAuthenticated } = useSelector((state) => state.auth)

  return (
    <>
      <NavBar isAuthenticated={isAuthenticated} />
      <div className='container w-75 mt-4'>
        <Routes>
          <Route element={<LoginRoute isAuthenticated={isAuthenticated} />}>
            <Route path='login' element={<Login />} />
            <Route path='register' element={<Register />} />
          </Route>
          <Route element={<ProtectedRoute isAuthenticated={isAuthenticated} />}>
            <Route path='' element={<Dashboard />} />
            <Route path='appts' element={<Appointments />} />
            <Route path='myAppts' element={<MyAppointments />} />
            <Route path='devices' element={<Devices />} />
            <Route path='devices/add' element={<AddDevice />} />
          </Route>
          <Route path="*" element={<Navigate to='' />} />
        </Routes>
        <ToastContainer />
      </div>
    </>
  );
}

function LoginRoute({ isAuthenticated }) {
  if (isAuthenticated) {
    return <Navigate to='/dashboard' replace />
  }
  else {
    return <Outlet />
  }
}


function ProtectedRoute({ isAuthenticated }) {
  if (isAuthenticated) {
    return <Outlet />
  }
  else {
    return <Navigate to='/login' replace />
  }
}


export default App;
