import axios from 'axios'
import { useState, useEffect } from "react"
import { getCSRF } from "../features/auth/authService"

export default function AddDevice({ toast }) {
    const [formData, setformData] = useState({
        mac: '',
        type: ''
    })

    const { mac, type } = formData
    const [message, setMessage] = useState('')


    function onChange(e) {
        setformData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value
        }))
    }

    async function onSubmit(e) {
        e.preventDefault()
        let userData = {
            mac,
            type
        }
        const config = getCSRF()
        const response = await axios.post('device/register', userData, config)
        if (response.status === 200) {
            setMessage(response.data.message)
        }
        else {
            setMessage(response.data.error)
        }
        console.log('registering device with following data:')
        console.log(userData)
    }


    return (
        <div className="container auth-box">
            <div className="mb-5">
                <h1>Register new device</h1>
                <p className="lead">enter the MAC addres and device type</p>
            </div>
            <hr />
            <form onSubmit={onSubmit}>
                <div className="mb-3">
                    <p className="lead fw-bold">Mac Address</p>
                    <label className="form-label">ex. 44:38:39:ff:ef:57</label>
                    <input
                        autoComplete="off"
                        type="text"
                        className="form-control"
                        name="mac" id="mac"
                        placeholder="mac address"
                        onChange={onChange}
                        value={mac}
                    />
                </div>
                <hr />
                <div className="mb-4">
                    <p className="lead fw-bold">Device type</p>
                    <label className="form-label">ex. MacBook Pro 14' 2022</label>
                    <input
                        autoComplete="off"
                        type="text"
                        className="form-control"
                        name="type"
                        id="type"
                        placeholder="device type"
                        onChange={onChange}
                        value={type}
                    />
                </div>
                {/* <hr /> */}
                <div className="">
                    <button type="submit" className="btn btn-secondary">Save</button>
                </div>
            </form>
        </div>
    )
}
