import { useEffect, useState } from 'react'
import { useDispatch, useSelector} from 'react-redux'
import { bookAppt, getAppts } from '../features/appt/apptSlice'



export function format(timeslot) {
    return String(timeslot * 2).padStart(2, '0') + ':00' + ' - ' + String(timeslot * 2 + 2).padStart(2, '0') + ':00'
}

function AppointmentsRow({ booked, timeslot, machinesCount, onBook }) {
    let machines = []

    for (let i = 0; i < machinesCount; i++) {
        machines.push(
            <td key={i + 1}>
                {booked[i] ? booked[i] : <button data-timeslot={timeslot} data-machine={i} type="button" className="btn btn-outline-secondary" onClick={(e) => { onBook(e.target.dataset.machine, e.target.dataset.timeslot) }}>book</button>}
            </td>
        )
    }
    return (
        <tr>
            <td key={0}>
                {format(timeslot)}
            </td>
            {machines}
        </tr>
    )
}

function AppointmentsTable({ date, appts, machinesCount, onBook }) {
    let rows = []
    let appointments = new Array(12)
    for (let i = 0; i < 12; i++) {
        appointments[i] = new Array(machinesCount)
    }

    for (let id in appts) {
        appointments[appts[id].timeslot][appts[id].machine] = appts[id].room
    }

    for (let i = 0; i < 12; i++) {
        rows.push(
            <AppointmentsRow key={i} booked={appointments[i]} timeslot={i} machinesCount={machinesCount} onBook={onBook} />
        )
    }

    let machines_header = []
    for (let i = 0; i < machinesCount; i++) {
        machines_header.push(
            <th key={i + 1}>Machine {i + 1}</th>
        )
    }
    return (
        <>
            <h1 className='mb-3'>{date}</h1>
            <table className="table table-striped align-middle fs-5">
                <thead>
                    <tr>
                        <th key="0">Timeslot</th>
                        {machines_header}
                    </tr>
                </thead>
                <tbody className="table-group-divider">
                    {rows}
                </tbody>
            </table>
        </>
    )
}

function DateSelector({ date, onDateChange }) {
    return (
        <input className="mb-3" type="date" id="date" value={date} onChange={(e) => onDateChange(e.target.value)} />
    )
}

export default function Appointments() {
    // Get the current date
    let today = new Date();
    let day = today.getDate();
    let month = today.getMonth() + 1;
    let year = today.getFullYear();
    if (month < 10) month = "0" + month;
    if (day < 10) day = "0" + day;
    today = year + "-" + month + "-" + day;
    const [date, setDate] = useState(today)

    // Get the appointments from the server and set up machines count
    const machinesCount = 2

    const {appointments} = useSelector((state) => state.appt)

    const dispatch = useDispatch()

    useEffect(() => {
        const userData = {
            date
        }
        dispatch(getAppts(userData))

    }, [date, dispatch, getAppts])

    async function onBook(machine, timeslot) {
        const userData = {
            date,
            timeslot,
            machine
        }
        dispatch(bookAppt(userData))
        dispatch(getAppts(userData))
    }

    return (
        <div className='text-center'>
            <DateSelector date={date} onDateChange={setDate} />
            <AppointmentsTable date={date} appts={appointments} machinesCount={machinesCount} onBook={onBook} />
        </div>
    )
}
